import React, {useEffect, useState} from 'react';
import './Dashboard.css';
import 'bootstrap/dist/css/bootstrap.css';
import {NavLink} from "react-router-dom";
import MenuItem from "./MenuItem";


const menuItems = [
    {name: 'Leads', exact:true, to: '/leads', iconClassName:"zmdi zmdi-view-dashboard", subMenus: [{ name: "Total Leads", exact:true, to: '/leads' }, { name: "Lead Status", to: '/leads/leadstatus' }]},
    {name: 'Reports', to: '/reports', iconClassName:"zmdi zmdi-view-toc"},
    {name: 'Profiles', to: '/profiles', iconClassName:"zmdi zmdi-account-circle"},
]

const Dashboard =(props) => {

    useEffect(() => {})


    const [inactive, setInactive] = useState(false);


    useEffect(() => {
        if(inactive){
            document.querySelectorAll('submenu').forEach(el => {
                el.classList.remove('active');
            })
        }

        props.onCollapse(inactive);

    }, [inactive])


    return(
        <div className={`side-menu ${inactive ?  'inactive' : '' }`}>
            <div className={"top-section"}>
                <div className={'logo'}>
                    <img src={require('../images/MicrosoftTeams-image (1).png')} />
                </div>
                <div className={'toggle-menu'}>
                    <button onClick={() => setInactive(!inactive)} className={'btn'}>
                        {
                            inactive ? <i className="zmdi zmdi-caret-left-circle"> </i> : <i className="zmdi zmdi-caret-right-circle"> </i>
                        }

                    </button>
                </div>
            </div>

            <div className={'divider'}> </div>

            <div className={'dashboard-text'}>
                <div className={'dashboard-icon'}>
                    <i className="zmdi zmdi-collection-bookmark" style={{color: "bisque", marginTop:"40%", fontSize: "1.5rem" }}> &nbsp;  <span> DASHBOARD </span>  </i>
                </div>
               <div className={'main-menu'}>
                    <ul>


                        {
                            menuItems.map((menuItem, index) =>
                                <MenuItem
                                    key={index} name={menuItem.name} exact={menuItem.exact} iconClassName={menuItem.iconClassName} to={menuItem.to} subMenus={menuItem.subMenus || [ ]}
                                    onClick={() => {
                                        if(inactive){
                                            setInactive(false);
                                        }
                                    }
                                    }
                                />)
                        }



                        {/*<li>*/}
                        {/*    <i className="zmdi zmdi-view-dashboard" style={{color: "bisque", fontSize: "1rem", marginLeft: "40%", marginRight: '-38.5%', display: "inline-block" }}> </i>*/}
                        {/*    <a className={'menu-item'}> <span> Leads </span> </a>*/}
                        {/*    <ul className={"submenu"}>*/}
                        {/*        <li>*/}
                        {/*            <a className={'sub-menu-item'}> <span> Total Leads </span> </a>*/}
                        {/*        </li>*/}
                        {/*        <li>*/}
                        {/*            <a className={'sub-menu-item'}> <span> Lead Status </span> </a>*/}
                        {/*        </li>*/}
                        {/*    </ul>*/}
                        {/*    <div className={'divider-two'}> </div>*/}
                        {/*</li>*/}
                        {/*<li>*/}
                        {/*    <i className="zmdi zmdi-view-toc" style={{color: "bisque", fontSize: "1.2rem", marginLeft: "40%", marginRight: '-38.5%', display: "inline-block" }}> </i>*/}
                        {/*    <a className={'menu-item'}> <span> Reports </span> </a>*/}
                        {/*    <div className={'divider-two'}> </div>*/}
                        {/*</li>*/}
                        {/*<li>*/}
                        {/*    <i className="zmdi zmdi-account-circle" style={{color: "bisque", fontSize: "1rem", marginLeft: "40%", marginRight: '-38.5%', display: "inline-block" }}> </i>*/}
                        {/*    <a className={'menu-item'}> <span> Profiles </span> </a>*/}
                        {/*    <div className={'divider-two'}> </div>*/}
                        {/*</li>*/}


                    </ul>

                </div>
            </div>

            <div className={'side-menu-footer'}>
                <div className={'footer-avatar'}>
                    <img src={require('../images/one-round-modified.png')} />
                </div>
                <div className={'user-info'}>
                    <h5>Testuser One</h5>
                    <p>testuserone@gmail.com</p>
                </div>
            </div>

        </div>
    )
}

export default Dashboard;
