import React, {useEffect, useState} from 'react';
import './Lead.css';
import 'bootstrap/dist/css/bootstrap.css';
import { CSVLink } from 'react-csv';
// import { jsPDF } from 'jspdf';
// import Picker from 'react-month-picker';
// import MonthPicker from './MonthPicker';
// import { ExcelExport } from '@progress/kendo-react-excel-export';
// import { Grid, GridColumn, GridToolbar } from "@progress/kendo-react-grid";

const Report =() => {

    const [agentData, setAgentData] = useState();

    const date = new Date();
    const currentMonth = date.getMonth();

    const [month, setMonth] = useState(currentMonth);

    const handleMonthChange = (e) => {
        setMonth(e.target.value);
    }


    const callReports = async() => {
        try{
            const res = await fetch('/reports', {
                method: "GET",
                headers: {
                    Accept: "application/json",
                    "Content-Type" : "application/json"
                },
                credentials: "include"
            });

            const data = await res.json();
            setAgentData(data);
            console.log("data", data);


        } catch(err) {
            console.log(err);
        }
    }

    useEffect(() => {
        callReports();
    }, []);


    //  const range = {
    //      min: { year: 2020, month: 3 },
    //      max: { year: 2025, month: 2 }
    //     };



//   const _export = React.useRef(null);

//   const excelExport = () => {
//     if (_export.current !== null) {
//       _export.current.save();
//     }
//   };

    // const GeneratePdf = () => {

    //     const doc =  new jsPDF("p", "pt", "a4");
    //     doc.html(document.querySelector("#content"),{
    //         callback: function(pdf){
    //             pdf.save("reports.pdf");
    //         }
    //     })
    // }


    const data = JSON.stringify(agentData);
    const headers = [
        {label: 'Name', key:'firstname' },
        {label: 'Phone Number', key:'phonenumber' },
        {label: 'Course name', key:'interestedcourse' },
        {label: 'Status', key:'status' },
        {label: 'Comments', key:'notes' }
    ]

    const csvReport = {
        filename: 'Report.csv',
        headers: headers,
        data: data
    };


    return(
         <div id='content'>
            {/* <ExcelExport data={agentData} ref={_export} > */}
           
            {/* <h2> This is reports </h2> */}

            <div className={'rls-top-section'}>
                <div className={"row"}>
                    <div className={'topbar'}>
                        <div className={"col"}>
                            <i className="zmdi zmdi-account-circle" style={{color: "bisque", fontSize: "1.8rem", marginRight: '40%'}}>  </i>
                            <p style={{  fontSize: "1rem", marginLeft: "20%" , width: "10vw", marginTop: "-2.9vh" }}> &nbsp;&nbsp; &nbsp; Total Leads &nbsp; -  </p>
                        </div>
                    <div className={"col"}>
                        <p style={{ display:"flex", color: "darkblue" , width: "10vw",  fontSize: "0.8rem", marginLeft: "8%", marginTop: "0.4vh" }}> (150) </p>  {/*add no of leads dynamically {status} from backend*/}
                    </div>

                     <div className={"col"}>
                        <button className={'btn-secondary'} style={{ backgroundColor: "white", borderRadius: "10%", padding: "5%", width:"780%", marginLeft: "955%"}}
                        //  onClick={PostData}
                         > 
                           &nbsp; <i class="zmdi zmdi-share"> </i>
                         </button>
                     {/* <MonthPicker  /> */}
                    </div>

                    <div className={"col"}>
                        <button className={'btn-secondary'} style={{ backgroundColor: "green", borderRadius: "10%", padding: "5%", width:"280%", marginLeft: "720%", marginTop: "-0.2vh"}}
                        //  onClick ={ 
                        //       GeneratePdf
                        //     }
                         > 
                          <CSVLink {...csvReport}> 
                              Export &nbsp; <i class="zmdi zmdi-share"> </i>
                          </CSVLink>
                         </button>
                    </div>
                    </div>
                </div>
                <br/>
            </div>


          <div className={"rls-body"}> 
            <div className={'top-row'}>
                <div className={'row'}>
                    <div className={'col'}>
                        <h6>Name</h6>
                    </div>
                    <div className={'col'}>
                        <h6>Phone Number</h6>
                    </div>
                    <div className={'col'}>
                        <h6>Course Name</h6>
                    </div>
                    <div className={'col'}>
                        <h6>Status</h6>
                    </div>
                    <div className={'col'}>
                        <h6>Comments</h6>
                    </div>
                    <div className={'col'}>
                        <h6> &nbsp; </h6>
                    </div>
                </div>
            </div>

            <div>
                {
                    !!agentData &&
                    agentData.map((agent) =>

                        <div className={'body-row'}>
                            <div className={'row'}>
                                <div className={'col'}>
                                    <h6>{agent.firstname}</h6>
                                </div>
                                <div className={'col'}>
                                    <h6>{agent.phonenumber}</h6>
                                </div>
                                <div className={'col'}>
                                    <h6>{agent.interestedcourse}</h6>
                                </div>
                                <div className={'col'}>
                                    <h6>{agent.status}</h6>
                                </div>
                                <div className={'col'}>
                                    <h6>{agent.notes}</h6>
                                </div>
                            </div>
                        </div>

                    )
                }
            </div>
            </div>
  {/* </ExcelExport> */}
       </div>
    )
}

export default Report;
