import React, {useEffect, useState} from 'react';
import './Lead.css';
import 'bootstrap/dist/css/bootstrap.css';
import Picker from 'react-month-picker';


const Report =() => {

    const [agentData, setAgentData] = useState();

    const date = new Date();
    const currentMonth = date.getMonth();

    const [month, setMonth] = useState(currentMonth);

    const handleMonthChange = (e) => {
        setMonth(e.target.value);
    }


    const callReports = async() => {
        try{
            const res = await fetch('/reports', {
                method: "GET",
                headers: {
                    Accept: "application/json",
                    "Content-Type" : "application/json"
                },
                credentials: "include"
            });

            const data = await res.json();
            setAgentData(data);
            console.log("data", data);


        } catch(err) {
            console.log(err);
        }
    }

    useEffect(() => {
        callReports();
    }, []);



    return(
        <div>
            {/* <h2> This is reports </h2> */}

            <div className={'rls-top-section'}>
                <div className={"row"}>
                    <div className={'topbar'}>
                        <div className={"col"}>
                            <i className="zmdi zmdi-account-circle" style={{color: "bisque", fontSize: "1.8rem", marginRight: '40%'}}>  </i>
                            <p style={{  fontSize: "1rem", marginLeft: "20%" , width: "10vw", marginTop: "-2.9vh" }}> &nbsp;&nbsp; &nbsp; Total Leads &nbsp; -  </p>
                        </div>
                    <div className={"col"}>
                        <p style={{ display:"flex", color: "darkblue" , width: "10vw",  fontSize: "0.8rem", marginLeft: "8%", marginTop: "0.5vh" }}> (150) </p>  {/*add no of leads dynamically {status} from backend*/}
                    </div>

                     <div className={"col"}>
                        <button className={'btn-secondary'} style={{ backgroundColor: "white", borderRadius: "10%", padding: "5%", width:"280%", marginLeft: "9s55%"}}
                        //  onClick={PostData}
                         > 
                         Month &nbsp; <i class="zmdi zmdi-share"> </i>
                         </button>
                         <label><b>Pick A Month</b><span>(Available years: 2008, 2011, 2012, 2014, 2016)</span></label>
                    <div className="edit">
                        <Picker
                            value={month}
                            onChange={handleMonthChange}
                        >
                            {/* <MonthBox value={makeText(singleValue)} onClick={this.handleClickMonthBox} /> */}
                        </Picker>
                    </div>
                    </div>

                    <div className={"col"}>
                        <button className={'btn-secondary'} style={{ backgroundColor: "green", borderRadius: "10%", padding: "5%", width:"280%", marginLeft: "720%"}}
                        //  onClick={PostData}
                         > 
                         Export &nbsp; <i class="zmdi zmdi-share"> </i>
                         </button>
                    </div>
                    </div>
                </div>
                <br/>
            </div>


            <div className={'top-row'}>
                <div className={'row'}>
                    <div className={'col'}>
                        <h6>Name</h6>
                    </div>
                    <div className={'col'}>
                        <h6>Phone Number</h6>
                    </div>
                    <div className={'col'}>
                        <h6>Course Name</h6>
                    </div>
                    <div className={'col'}>
                        <h6>Status</h6>
                    </div>
                    <div className={'col'}>
                        <h6>Comments</h6>
                    </div>
                    <div className={'col'}>
                        <h6> &nbsp; </h6>
                    </div>
                </div>
            </div>

            <div>
                {
                    !!agentData &&
                    agentData.map((agent) =>

                        <div className={'body-row'}>
                            <div className={'row'}>
                                <div className={'col'}>
                                    <h6>{agent.firstname}</h6>
                                </div>
                                <div className={'col'}>
                                    <h6>{agent.phonenumber}</h6>
                                </div>
                                <div className={'col'}>
                                    <h6>{agent.interestedcourse}</h6>
                                </div>
                                <div className={'col'}>
                                    <h6>{agent.status}</h6>
                                </div>
                                <div className={'col'}>
                                    <h6>{agent.notes}</h6>
                                </div>
                            </div>
                        </div>

                    )
                }
            </div>

        </div>
    )
}

export default Report;
