import React, {useEffect, useState} from 'react';
import './Lead.css';
import 'bootstrap/dist/css/bootstrap.css';
import { CSVLink } from 'react-csv';
import { DatePicker } from 'react-datepicker';
import { lightBlue } from '@material-ui/core/colors';

const Report =() => {

    const [agentData, setAgentData] = useState();
 
    const [startDate, setStartDate] = useState(new Date());


    const callReports = async() => {
        try{
            const res = await fetch('/reports', {
                method: "GET",
                headers: {
                    Accept: "application/json",
                    "Content-Type" : "application/json"
                },
                credentials: "include"
            });

            const data = await res.json();
            setAgentData(data);
            console.log("data", data);


        } catch(err) {
            console.log(err);
        }
    }

    useEffect(() => {
        callReports();
    }, []);



    const data1 = [agentData];
    const  data = JSON.stringify(data1);
    const headers = [
        {label: 'Name', key:'firstname' },
        {label: 'Phone Number', key:'phonenumber' },
        {label: 'Course name', key:'interestedcourse' },
        {label: 'Status', key:'status' },
        {label: 'Comments', key:'notes' }
    ]

    const csvReport = {
        filename: 'Report.csv',
        headers: headers,
        data: data
    };


    return(
         <div id='content'>

            <div className={'rls-top-section'}>
                <div className={"row"}>
                    <div className={'topbar'}>
                        <div className={"col"}>
                            <i className="zmdi zmdi-account-circle" style={{color: "bisque", fontSize: "1.8rem", marginRight: '40%'}}>  </i>
                            <p style={{  fontSize: "1rem", marginLeft: "20%" , width: "10vw", marginTop: "-2.9vh" }}> &nbsp;&nbsp; &nbsp; Total Leads &nbsp; -  </p>
                        </div>
                    <div className={"col"}>
                        <p style={{ display:"flex", color: "darkblue" , width: "10vw",  fontSize: "0.8rem", marginLeft: "8%", marginTop: "0.4vh" }}> (150) </p>  {/*add no of leads dynamically {status} from backend*/}
                    </div>

                     <div className={"col"} style={{backgroundColor: "lightBlue"}}>
                         {
                             <DatePicker
                          selected={startDate}
                          onChange={(date) => setStartDate(date)}
                          dateFormat="MM/yyyy"
                           showMonthYearPicker
                        />

                         }

                        {/* <DatePicker
                          selected={startDate}
                          onChange={(date) => setStartDate(date)}
                          dateFormat="MM/yyyy"
                           showMonthYearPicker
                        /> */}

                        {/* <button className={'btn-secondary'} style={{ backgroundColor: "white", borderRadius: "10%", padding: "5%", width:"780%", marginLeft: "955%"}}
                        //  onClick={PostData}
                         > 
                           &nbsp; <i class="zmdi zmdi-share"> </i>
                         </button> */}
                         
                    </div>

                    <div className={"col"}>
                        <button className={'btn-secondary'} style={{ backgroundColor: "green", borderRadius: "10%", padding: "6%", width:"280%", marginLeft: "720%", marginTop: "-0.2vh"}}
                         > 
                          <CSVLink {...csvReport} style={{color:"whitesmoke"}}> 
                              Export &nbsp; <i class="zmdi zmdi-share"> </i>
                          </CSVLink>
                         </button>
                    </div>
                    </div>
                </div>
                <br/>
            </div>


          <div className={"rls-body"}> 
            <div className={'top-row'}>
                <div className={'row'}>
                    <div className={'col'}>
                        <h6>Name</h6>
                    </div>
                    <div className={'col'}>
                        <h6>Phone Number</h6>
                    </div>
                    <div className={'col'}>
                        <h6>Course Name</h6>
                    </div>
                    <div className={'col'}>
                        <h6>Status</h6>
                    </div>
                    <div className={'col'}>
                        <h6>Comments</h6>
                    </div>
                    <div className={'col'}>
                        <h6> &nbsp; </h6>
                    </div>
                </div>
            </div>

            <div>
                {
                    !!agentData &&
                    agentData.map((agent) =>

                        <div className={'body-row'}>
                            <div className={'row'}>
                                <div className={'col'}>
                                    <h6>{agent.firstname}</h6>
                                </div>
                                <div className={'col'}>
                                    <h6>{agent.phonenumber}</h6>
                                </div>
                                <div className={'col'}>
                                    <h6>{agent.interestedcourse}</h6>
                                </div>
                                <div className={'col'}>
                                    <h6>{agent.status}</h6>
                                </div>
                                <div className={'col'}>
                                    <h6>{agent.notes}</h6>
                                </div>
                            </div>
                        </div>

                    )
                }
            </div>
            </div>
       </div>
    )
}

export default Report;
