#!/usr/bin/env node
const program = require('commander')
const { executeRunners } = require('../helpers')
const { prettier } = require('../runners')

program
  .description('Format javascript code')
  .option('--prettier-options <options>', 'Overwrite prettier options')
  .option('--extend-prettier-options <options>', 'Extend prettier options')
  .parse(process.argv)

const {
  prettierOptions,
  extendPrettierOptions,
} = program

executeRunners([
  prettier({ prettierOptions, extendPrettierOptions })
])
