#!/usr/bin/env node
const program = require('commander')
const { executeRunners } = require('../helpers')
const { info } = require('../runners')

program
  .description('Run scripts info')
  .option('--info-options <options>', 'Overwrite scripts info options')
  .option('--extend-info-options <options>', 'Extend scripts info options')
  .parse(process.argv)

const {
  infoOptions,
  extendInfoOptions,
} = program

executeRunners([
  info({ infoOptions, extendInfoOptions })
])
