#!/usr/bin/env node
const program = require('commander')
const { executeRunners } = require('../helpers')
const { jest } = require('../runners')

program
  .description('Run jest test suit')
  .option('--jest-options <options>', 'Overwrite jest options')
  .option('--extend-jest-options <options>', 'Extend jest options')
  .parse(process.argv)

const {
  jestOptions,
  extendJestOptions,
} = program

executeRunners([
  jest({ jestOptions, extendJestOptions })
])
