#!/usr/bin/env node
const program = require('commander')
const path = require('path')

const packageJson = require(path.resolve(__dirname, '../package.json'))

program
  .version(packageJson.version)
  .command('test [options]', 'Call package test suit, jest')
  .command('lint [options]', 'Call package linters')
  .command('format [options]', 'Format javascript code')
  .command('webpack [options]', 'Bundle code using webpack and babel-preset-crow')
  .command('rollup [options]', 'Bundle code using rollup and babel-preset-crow')
  .command('transpile [options]', 'Transpile code using babel-preset-crow')
  .parse(process.argv)
