const jsRule = require('./js-rule')

module.exports = ({
  targets,
  loose,
  debug,
  spec,
  useBuiltIns,
  disableCSS = false,
}) => (config) => {
  config.module.rules[0].oneOf = config.module.rules[0].oneOf.concat([
    jsRule({
      targets,
      loose,
      debug,
      spec,
      useBuiltIns,
      disableCSS,
    })
  ])

  return(config)
}
