const ManifestPlugin = require('webpack-manifest-plugin')

module.exports = ({
  manifestFile = 'assets/manifest.json',
  disableManifest = false,
}) => (config) => {
  if (!disableManifest) {
    config.plugins = config.plugins.concat([
      new ManifestPlugin({
        fileName: manifestFile
      })
    ])
  }

  return(config)
}
