const parseOptions = ({
  defaultParams = [],
  overwriteOptions = '',
  extendOptions = '',
} = {}) => {
  let params = overwriteOptions === ''
    ? defaultParams
    : overwriteOptions.split(/\s+/)

  params = extendOptions === ''
    ? params
    : params.concat(extendOptions.split(/\s+/))

  return params
}

module.exports = parseOptions
