  webpack(
    webpackConfig(options),
    (err, stats) => {
    if (err || stats.hasErrors()) {
      console.error(chalk.red.bold('â¯ ğŸ’£ Error: '), err)
      console.error(stats.toString({ colors: true, errorDetails: true }))
      throw err
    }
  })
