const { spawnPromise, parseOptions } = require('../helpers')

module.exports = ({ nspOptions, extendNspOptions }) => {
  const params = parseOptions({
    overwriteOptions: nspOptions,
    extendOptions: extendNspOptions,
    defaultParams: [
      'check'
    ]
  })

  return spawnPromise({ bin: 'nsp', params })
}
